import"./style-BD-upOib.js";import{G as e}from"./cookies-lEVMIMuA.js";document.addEventListener("DOMContentLoaded",()=>{i()});window.LoginButton=()=>{e("sessionId")==""?window.location.href="login.html":window.location.href="profile.html"};async function i(){if(e("sessionId")=="")return;const o=document.getElementById("username-box");let n=e("sessionId");n=n.replace(/['"]+/g,"").toUpperCase();const t=await fetch(`https://localhost:7168/api/LoginDetails/GetUsername?sessionId=${n}`);o.innerText=await t.text()}window.OptionClicked=o=>{e("sessionId")==""?window.location.href="login.html":window.location.href=o};
